import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './hooks/useAuth';
import AdminLogin from './components/admin/AdminLogin';
import BookingForm from './components/booking/BookingForm';
import AdminDashboard from './components/admin/AdminDashboard';
import Layout from './components/common/Layout';
import { initializeBusinessHours } from './utils/initData';

function App() {
  const { user, loading, isAuthenticated, isAdminUser } = useAuth();

  // 認証状態を読み込み中
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-salon-500 mx-auto mb-4"></div>
          <p className="text-gray-600">読み込み中...</p>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* 公開ルート：予約ページ */}
          <Route 
            path="/" 
            element={
              <Layout showAdminLink={true}>
                <BookingForm />
              </Layout>
            } 
          />

          {/* 管理者ログインページ */}
          <Route 
            path="/admin/login" 
            element={
              isAuthenticated ? (
                <Navigate to="/admin" replace />
              ) : (
                <AdminLogin />
              )
            } 
          />

          {/* 管理者ダッシュボード（認証が必要） */}
          <Route 
            path="/admin" 
            element={
              isAuthenticated && isAdminUser ? (
                <Layout showAdminLink={false}>
                  <AdminDashboard />
                </Layout>
              ) : (
                <Navigate to="/admin/login" replace />
              )
            } 
          />

          {/* 存在しないパスは予約ページにリダイレクト */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
