import React, { useState, useEffect } from 'react';
import { Tab } from '@headlessui/react';
import { 
  CalendarDaysIcon, 
  ClockIcon, 
  UserGroupIcon,
  TrashIcon,
  PencilIcon
} from '@heroicons/react/24/outline';
import CalendarView from '../booking/CalendarView';
import BusinessHours from './BusinessHours';
import { useBookings } from '../../hooks/useBookings';
import { 
  formatDateWithWeekday, 
  formatTimeRange, 
  generateWeekDates,
  getWeekStart
} from '../../utils/dateUtils';

function classNames(...classes) {
  return classes.filter(Boolean).join(' ');
}

const AdminDashboard = () => {
  const [selectedTab, setSelectedTab] = useState(0);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [weekBookings, setWeekBookings] = useState([]);
  const [isDeleting, setIsDeleting] = useState(false);

  const { 
    loadBookingsByDateRange, 
    removeBooking, 
    loading, 
    error,
    clearError 
  } = useBookings();

  // タブメニュー
  const tabs = [
    { name: '予約カレンダー', icon: CalendarDaysIcon },
    { name: '予約一覧', icon: UserGroupIcon },
    { name: '営業時間設定', icon: ClockIcon }
  ];

  // 現在の週の予約を取得
  useEffect(() => {
    const loadWeekBookings = async () => {
      const weekStart = getWeekStart(selectedDate);
      const weekDates = generateWeekDates(weekStart);
      const startDate = weekDates[0];
      const endDate = weekDates[6];

      const result = await loadBookingsByDateRange(startDate, endDate);
      if (result.success) {
        setWeekBookings(result.bookings);
      }
    };

    loadWeekBookings();
  }, [selectedDate, loadBookingsByDateRange]);

  // 日時選択ハンドラ（管理者モード）
  const handleDateTimeSelect = (date, time) => {
    setSelectedDate(date);
    // 管理者モードでは予約作成は行わない
  };

  // 予約削除
  const handleDeleteBooking = async (bookingId, customerName, date, time) => {
    if (!window.confirm(`${customerName}様の予約を削除しますか？\n日時: ${date} ${time}`)) {
      return;
    }

    setIsDeleting(true);
    const result = await removeBooking(bookingId);

    if (result.success) {
      // 予約一覧を再読み込み
      const weekStart = getWeekStart(selectedDate);
      const weekDates = generateWeekDates(weekStart);
      const startDate = weekDates[0];
      const endDate = weekDates[6];

      const updatedResult = await loadBookingsByDateRange(startDate, endDate);
      if (updatedResult.success) {
        setWeekBookings(updatedResult.bookings);
      }
    } else {
      alert('予約の削除に失敗しました: ' + result.error);
    }

    setIsDeleting(false);
  };

  // 予約統計情報
  const getBookingStats = () => {
    const today = new Date();
    const todayStr = today.toISOString().split('T')[0];

    const todayBookings = weekBookings.filter(booking => booking.date === todayStr);
    const weekTotal = weekBookings.length;

    return {
      today: todayBookings.length,
      week: weekTotal
    };
  };

  const stats = getBookingStats();

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
      {/* ヘッダー */}
      <div className="mb-8">
        <h1 className="text-2xl font-bold text-gray-900">管理ダッシュボード</h1>
        <p className="mt-1 text-sm text-gray-600">
          予約管理と店舗設定を行えます
        </p>
      </div>

      {/* 統計情報 */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <CalendarDaysIcon className="h-6 w-6 text-salon-600" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    本日の予約
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    {stats.today}件
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>

        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="p-5">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <UserGroupIcon className="h-6 w-6 text-salon-600" />
              </div>
              <div className="ml-5 w-0 flex-1">
                <dl>
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    今週の予約
                  </dt>
                  <dd className="text-lg font-medium text-gray-900">
                    {stats.week}件
                  </dd>
                </dl>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* エラー表示 */}
      {error && (
        <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
          <p className="text-red-700 text-sm">{error}</p>
          <button
            onClick={clearError}
            className="text-red-600 hover:text-red-800 text-sm underline ml-2"
          >
            閉じる
          </button>
        </div>
      )}

      {/* タブメニュー */}
      <Tab.Group selectedIndex={selectedTab} onChange={setSelectedTab}>
        <Tab.List className="flex space-x-1 rounded-xl bg-salon-100 p-1">
          {tabs.map((tab, index) => (
            <Tab
              key={tab.name}
              className={({ selected }) =>
                classNames(
                  'w-full rounded-lg py-2.5 text-sm font-medium leading-5',
                  'ring-white ring-opacity-60 ring-offset-2 ring-offset-salon-400 focus:outline-none focus:ring-2',
                  selected
                    ? 'bg-white text-salon-700 shadow'
                    : 'text-salon-600 hover:bg-white/[0.12] hover:text-salon-700'
                )
              }
            >
              <div className="flex items-center justify-center space-x-2">
                <tab.icon className="w-4 h-4" />
                <span>{tab.name}</span>
              </div>
            </Tab>
          ))}
        </Tab.List>

        <Tab.Panels className="mt-6">
          {/* 予約カレンダー */}
          <Tab.Panel>
            <div className="space-y-6">
              <div>
                <h2 className="text-lg font-medium text-gray-900 mb-4">
                  予約カレンダー
                </h2>
                <CalendarView
                  selectedDate={selectedDate}
                  selectedTime={null}
                  onDateTimeSelect={handleDateTimeSelect}
                  mode="admin"
                />
              </div>
            </div>
          </Tab.Panel>

          {/* 予約一覧 */}
          <Tab.Panel>
            <div className="space-y-6">
              <div>
                <h2 className="text-lg font-medium text-gray-900 mb-4">
                  今週の予約一覧
                </h2>

                {loading ? (
                  <div className="text-center py-8">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-salon-500 mx-auto"></div>
                    <p className="text-gray-600 mt-2">読み込み中...</p>
                  </div>
                ) : weekBookings.length === 0 ? (
                  <div className="text-center py-8">
                    <UserGroupIcon className="mx-auto h-12 w-12 text-gray-400" />
                    <h3 className="mt-2 text-sm font-medium text-gray-900">
                      予約がありません
                    </h3>
                    <p className="mt-1 text-sm text-gray-500">
                      今週の予約はまだありません。
                    </p>
                  </div>
                ) : (
                  <div className="bg-white shadow overflow-hidden sm:rounded-md">
                    <ul className="divide-y divide-gray-200">
                      {weekBookings.map((booking) => (
                        <li key={booking.id}>
                          <div className="px-4 py-4 sm:px-6">
                            <div className="flex items-center justify-between">
                              <div className="flex items-center">
                                <div className="flex-shrink-0">
                                  <div className="h-10 w-10 rounded-full bg-salon-100 flex items-center justify-center">
                                    <UserGroupIcon className="h-5 w-5 text-salon-600" />
                                  </div>
                                </div>
                                <div className="ml-4">
                                  <div className="flex items-center">
                                    <p className="text-sm font-medium text-salon-600">
                                      {booking.customerName}
                                    </p>
                                  </div>
                                  <div className="flex items-center text-sm text-gray-500 mt-1">
                                    <CalendarDaysIcon className="flex-shrink-0 mr-1.5 h-4 w-4" />
                                    <p>
                                      {formatDateWithWeekday(new Date(booking.date))} {formatTimeRange(booking.time)}
                                    </p>
                                  </div>
                                </div>
                              </div>
                              <div className="flex items-center space-x-2">
                                <button
                                  onClick={() => handleDeleteBooking(
                                    booking.id,
                                    booking.customerName,
                                    formatDateWithWeekday(new Date(booking.date)),
                                    formatTimeRange(booking.time)
                                  )}
                                  disabled={isDeleting}
                                  className="inline-flex items-center p-2 border border-transparent rounded-full shadow-sm text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 disabled:opacity-50"
                                >
                                  <TrashIcon className="h-4 w-4" />
                                </button>
                              </div>
                            </div>
                          </div>
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            </div>
          </Tab.Panel>

          {/* 営業時間設定 */}
          <Tab.Panel>
            <div className="space-y-6">
              <div>
                <h2 className="text-lg font-medium text-gray-900 mb-4">
                  営業時間設定
                </h2>
                <BusinessHours />
              </div>
            </div>
          </Tab.Panel>
        </Tab.Panels>
      </Tab.Group>
    </div>
  );
};

export default AdminDashboard;
