import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import CalendarView from './CalendarView';
import BookingConfirm from './BookingConfirm';
import { useBookings } from '../../hooks/useBookings';
import { formatDateWithWeekday, formatTimeRange, formatDateString } from '../../utils/dateUtils';

const BookingForm = () => {
  const [step, setStep] = useState(1); // 1: Êó•Á®ãÈÅ∏Êäû, 2: ÊÉÖÂ†±ÂÖ•Âäõ, 3: Á¢∫Ë™ç, 4: ÂÆå‰∫Ü
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [bookingResult, setBookingResult] = useState(null);

  const { addBooking, loading, error, clearError } = useBookings();

  const { 
    register, 
    handleSubmit, 
    formState: { errors }, 
    reset 
  } = useForm();

  // Êó•ÊôÇÈÅ∏Êäû„Éè„É≥„Éâ„É©
  const handleDateTimeSelect = (date, time) => {
    setSelectedDate(date);
    setSelectedTime(time);
    setStep(2); // ÊÉÖÂ†±ÂÖ•Âäõ„Çπ„ÉÜ„ÉÉ„Éó„Å´ÈÄ≤„ÇÄ
    clearError();
  };

  // „Éï„Ç©„Éº„É†ÈÄÅ‰ø°
  const onSubmit = async (data) => {
    if (!selectedDate || !selectedTime) return;

    setIsSubmitting(true);

    const bookingData = {
      date: formatDateString(selectedDate),
      time: selectedTime,
      customerName: data.customerName.trim()
    };

    try {
      const result = await addBooking(bookingData);

      if (result.success) {
        setBookingResult({
          success: true,
          bookingId: result.id,
          ...bookingData
        });
        setStep(4); // ÂÆå‰∫ÜÁîªÈù¢
      } else {
        setBookingResult({
          success: false,
          error: result.error
        });
