import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import CalendarView from './CalendarView';
import BookingConfirm from './BookingConfirm';
import { useBookings } from '../../hooks/useBookings';
import { formatDateWithWeekday, formatTimeRange, formatDateString } from '../../utils/dateUtils';

const BookingForm = () => {
  const [step, setStep] = useState(1); // 1: 日程選択, 2: 情報入力, 3: 確認, 4: 完了
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [bookingResult, setBookingResult] = useState(null);

  const { addBooking, loading, error, clearError } = useBookings();

  const { 
    register, 
    handleSubmit, 
    formState: { errors }, 
    reset 
  } = useForm();

  // 日時選択ハンドラ
  const handleDateTimeSelect = (date, time) => {
    setSelectedDate(date);
    setSelectedTime(time);
    setStep(2); // 情報入力ステップに進む
    clearError();
  };

  // フォーム送信
  const onSubmit = async (data) => {
    if (!selectedDate || !selectedTime) return;

    setIsSubmitting(true);

    const bookingData = {
      date: formatDateString(selectedDate),
      time: selectedTime,
      customerName: data.customerName.trim()
    };

    try {
      const result = await addBooking(bookingData);

      if (result.success) {
        setBookingResult({
          success: true,
          bookingId: result.id,
          ...bookingData
        });
        setStep(4); // 完了画面
      } else {
        setBookingResult({
          success: false,
          error: result.error
        });
      }
    } catch (error) {
      setBookingResult({
        success: false,
        error: '予約の作成に失敗しました'
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  // 最初からやり直し
  const handleRestart = () => {
    setStep(1);
    setSelectedDate(null);
    setSelectedTime(null);
    setBookingResult(null);
    reset();
    clearError();
  };

  // 前のステップに戻る
  const handleBack = () => {
    if (step > 1) {
      setStep(step - 1);
      clearError();
    }
  };

  // ステップインジケーター
  const StepIndicator = () => (
    <div className="flex items-center justify-center mb-6">
      <div className="flex items-center space-x-4">
        {[1, 2, 3, 4].map((stepNumber) => (
          <React.Fragment key={stepNumber}>
            <div className={`
              w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium
              ${step >= stepNumber 
                ? 'bg-salon-500 text-white' 
                : 'bg-gray-200 text-gray-600'
              }
            `}>
              {stepNumber}
            </div>
            {stepNumber < 4 && (
              <div className={`
                w-16 h-1 
                ${step > stepNumber ? 'bg-salon-500' : 'bg-gray-200'}
              `} />
            )}
          </React.Fragment>
        ))}
      </div>
    </div>
  );

  const StepLabels = () => (
    <div className="flex justify-between text-xs text-gray-600 mb-8 max-w-md mx-auto">
      <span className={step >= 1 ? 'text-salon-600 font-medium' : ''}>
        予約日程選択
      </span>
      <span className={step >= 2 ? 'text-salon-600 font-medium' : ''}>
        情報入力
      </span>
      <span className={step >= 3 ? 'text-salon-600 font-medium' : ''}>
        確認
      </span>
      <span className={step >= 4 ? 'text-salon-600 font-medium' : ''}>
        完了
      </span>
    </div>
  );

  return (
    <div className="max-w-4xl mx-auto p-4">
      {/* ヘッダー */}
      <div className="text-center mb-8">
        <h1 className="text-2xl font-bold text-gray-900 mb-2">
          予約ページ
        </h1>
        <p className="text-gray-600">
          ご希望の日時を選択して予約を完了してください
        </p>
      </div>

      {/* ステップインジケーター */}
      <StepIndicator />
      <StepLabels />

      {/* エラー表示 */}
      {error && (
        <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
          <p className="text-red-700 text-sm">{error}</p>
        </div>
      )}

      {/* ステップ1: 日程選択 */}
      {step === 1 && (
        <div className="space-y-6">
          <div className="text-center">
            <h2 className="text-lg font-semibold text-gray-900 mb-2">
              ご希望の日時を選択してください
            </h2>
            <p className="text-gray-600 text-sm">
              緑色の時間帯が予約可能です
            </p>
          </div>
          <CalendarView
            selectedDate={selectedDate}
            selectedTime={selectedTime}
            onDateTimeSelect={handleDateTimeSelect}
            mode="booking"
          />
        </div>
      )}

      {/* ステップ2: 情報入力 */}
      {step === 2 && (
        <div className="max-w-md mx-auto">
          <div className="text-center mb-6">
            <h2 className="text-lg font-semibold text-gray-900 mb-2">
              お客様情報を入力してください
            </h2>

            {/* 選択された日時の表示 */}
            <div className="bg-salon-50 border border-salon-200 rounded-lg p-4 mb-6">
              <div className="text-salon-700">
                <div className="font-medium">
                  📅 {formatDateWithWeekday(selectedDate)}
                </div>
                <div className="text-sm mt-1">
                  🕐 {formatTimeRange(selectedTime)}
                </div>
              </div>
            </div>
          </div>

          <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                お名前 <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                {...register('customerName', {
                  required: 'お名前を入力してください',
                  minLength: {
                    value: 1,
                    message: 'お名前を入力してください'
                  },
                  maxLength: {
                    value: 50,
                    message: 'お名前は50文字以内で入力してください'
                  }
                })}
                className="input-field"
                placeholder="山田 太郎"
              />
              {errors.customerName && (
                <p className="text-red-500 text-xs mt-1">
                  {errors.customerName.message}
                </p>
              )}
            </div>

            <div className="pt-4 space-y-3">
              <button
                type="submit"
                disabled={isSubmitting}
                className="btn-primary w-full"
              >
                {isSubmitting ? '予約中...' : '予約内容を確認する'}
              </button>

              <button
                type="button"
                onClick={handleBack}
                className="btn-secondary w-full"
              >
                日時を変更する
              </button>
            </div>
          </form>
        </div>
      )}

      {/* ステップ3: 確認（実際はステップ2で直接予約作成） */}

      {/* ステップ4: 完了 */}
      {step === 4 && bookingResult && (
        <div className="max-w-md mx-auto text-center">
          {bookingResult.success ? (
            <div className="space-y-6">
              <div className="text-green-600">
                <svg
                  className="w-16 h-16 mx-auto mb-4"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M5 13l4 4L19 7"
                  />
                </svg>
                <h2 className="text-xl font-bold mb-2">
                  🎉 予約が完了しました！
                </h2>
              </div>

              <div className="bg-green-50 border border-green-200 rounded-lg p-6">
                <h3 className="font-semibold text-green-800 mb-3">
                  【予約詳細】
                </h3>
                <div className="text-green-700 space-y-1">
                  <div>
                    📅 日時: {formatDateWithWeekday(selectedDate)} {formatTimeRange(selectedTime)}
                  </div>
                  <div>
                    👤 お客様: {bookingResult.customerName}
                  </div>
                </div>
              </div>

              <p className="text-gray-600 text-sm">
                予約が確定いたしました。<br />
                当日は時間に余裕を持ってお越しください。
              </p>

              <button
                onClick={handleRestart}
                className="btn-secondary"
              >
                新しい予約を作成する
              </button>
            </div>
          ) : (
            <div className="space-y-6">
              <div className="text-red-600">
                <svg
                  className="w-16 h-16 mx-auto mb-4"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                </svg>
                <h2 className="text-xl font-bold mb-2">
                  予約に失敗しました
                </h2>
              </div>

              <div className="bg-red-50 border border-red-200 rounded-lg p-4">
                <p className="text-red-700 text-sm">
                  {bookingResult.error}
                </p>
              </div>

              <button
                onClick={handleRestart}
                className="btn-primary"
              >
                最初からやり直す
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default BookingForm;
